import React, {Component} from "react"
import { Container, Button, Card, CardMedia, CardActions, CardContent,Typography} from '@material-ui/core';
import ThumbUpAltIcon from '@material-ui/icons/ThumbUpAlt';
import DeleteIcon from '@material-ui/icons/Delete';
import MoreHorizIcon from '@material-ui/icons/MoreHoriz';
import useStyles from './JSstyles/topicStyles';
import { ACCESS_LEVEL_GUEST, ACCESS_LEVEL_ADMIN, SERVER_HOST } from "../config/global_constants";
import axios from "axios"
//single topic


export default class topics extends Component 
{
    componentDidMount() 
    {
        this.props.topic.photos.map(photo => 
        {
            return axios.get(`${SERVER_HOST}/topics/topic_photo/${photo.filename}`)
            .then(res => 
            {         
                document.getElementById(photo._id).src = `data:;base64,${res.data.image}`                                                         
            })
            .catch(err =>
            {
                // do nothing
            })
        })
    }

    render() {
        const classes = useStyles();
        return (
              <Card className ={classes.card}>
                  <CardMedia className={classes.media} image={this.props.topic.photos} title={this.props.topic.title}/>
                  <div className={classes.overlay}>
                      <Typography variant ="h6">{this.props.topic.title}</Typography>

                  </div>
                  <div className={classes.overlay2}>
                      <Button style={{color:"white"}} size="small" onClick={() => {}}>
                            <MoreHorizIcon fontSize="default"/>
                      </Button>
                  </div>
                  <CardContent>
                        <Typography className={classes.title} variant="h5" gutterBottom>{this.props.topic.discription}</Typography>
                  </CardContent>
                  <CardActions className={classes.CardActions}>
                  {
                            localStorage.accessLevel > ACCESS_LEVEL_GUEST
                                ? 
                                <Button size="small" color ="primary" onClick={() => {}}>
                                <ThumbUpAltIcon fontSize="small" />
                                like 
                                {this.props.topic.likeCount}
                               </Button>

                                 : null
                    }
                      {
                            localStorage.accessLevel >= ACCESS_LEVEL_ADMIN
                                ? 
                                    <Button size="small" color ="primary" onClick={() => {}}>
                                    <DeleteIcon fontSize="small" />
                                    Delete
                                   </Button>

                                 : null
                    }

                  </CardActions>

              </Card>

        )
    }
}
